FunctionOracle.source:
CREATE OR REPLACE FUNCTION Find_Response_Time_f(p_client_id IN NUMBER DEFAULT NULL, p_software_name IN STRING DEFAULT NULL) RETURN NUMBER IS
v_response_time NUMBER(4) := NULL;
BEGIN
IF p_client_id IS NOT NULL AND p_software_name IS NOT NULL THEN
    SELECT SLA.Response_Time INTO v_response_time FROM SLA JOIN Contracts ON SLA.name = Contracts.SLA
    WHERE Contracts.Clinet_ID = p_client_id AND Contracts.Software = p_software_name AND Contracts.End_Date < SYSDATE;
END IF;
RETURN v_response_time;
END Find_Response_Time_f;
