TriggerOraclev10g.body:
DECLARE
v_response_time NUMBER(4);
BEGIN
SELECT Ticket_Number_SEQ.nextval INTO :NEW.Ticket_Number FROM DUAL;
SELECT SYSDATE INTO :NEW.Submission_Date FROM DUAL;
:NEW.Priority := "Normal";

v_response_time := Find_Response_Time_f(:NEW.Clinet_ID, :NEW.Software);
IF v_response_time IS NOT NULL THEN
:NEW.Due_Date := :NEW.Submission_Date + v_response_time/24;
END IF;
END;
